mapper.insert:

	SELECT ATCH_SEQ.NEXTVAL
	FROM DUAL

INSERT ALL

	INTO ATCH_FILE(
		 ATCH_ID
		, ATCH_SEQ
		, ATCH_MIME
		, ATCH_ORIGIN_NAME
		, ATCH_SAVE_NAME
		, ATCH_SIZE
		, ATCH_DATE
	)VALUES(
		#{atchId,jdbcType=NUMERIC}
		, #{idx}+1
		, #{attatch.atchMime,jdbcType=VARCHAR}
		, #{attatch.atchOriginName,jdbcType=VARCHAR}
		, #{attatch.atchSaveName,jdbcType=VARCHAR}
		, #{attatch.atchSize,jdbcType=NUMERIC}
		, SYSTIMESTAMP
	)

SELECT * FROM DUAL


	SELECT NVL(MAX(ATCH_SEQ), 0) + 1
	FROM ATCH_FILE
	WHERE ATCH_ID = #{atchId}

INSERT ALL

	INTO ATCH_FILE(
		 ATCH_ID
		, ATCH_SEQ
		, ATCH_MIME
		, ATCH_ORIGIN_NAME
		, ATCH_SAVE_NAME
		, ATCH_SIZE
		, ATCH_DATE
	)VALUES(
		#{atchId,jdbcType=NUMERIC}
		, #{idx}+#{startSeq}
		, #{attatch.atchMime,jdbcType=VARCHAR}
		, #{attatch.atchOriginName,jdbcType=VARCHAR}
		, #{attatch.atchSaveName,jdbcType=VARCHAR}
		, #{attatch.atchSize,jdbcType=NUMERIC}
		, SYSTIMESTAMP
	)

SELECT * FROM DUAL
